    // if (username !== null & username !== undefined)
    // {
    //   tempUsername=name
    //   username = localStorage.getItem('username').charAt(0).toUpperCase() + username.slice(1)  

    //   }
      
    // name = name.replace(username, '')
    // tempUsername = tempUsername.replace(username, '')
    
    // if (itemNO > 2)
    // {
    //   name+=' [Group]'
    // }

   
    return temp
  }


  render() {
    return (
      <div className='wrapTry' style={{fontSize:'15px'}}>
        
      <NavLink  to={`/${this.props.chatURL}`} style={{ color: "#fff" }}>      
      <li   >
        {localStorage.getItem('token') !== null ?
              <div > 
            <img style={{
            width: '50px',
            borderRadius: "50%",
            float: 'left',
            marginRight: '10px'
                }}
                  // src='http://emilcarlsson.se/assets/louislitt.png'
                  src={this.getImageDetail()}
                  // src={this.props.picURL}
                />
                
                <div className="meta">
                  <p className="name" >   
                    {this.getName()}
                  </p>
          </div>
        </div>
          :
    <p></p>
    
 
        }
    
        
</li>

        </NavLink>
        <br/>
</div>
    )
  }
}


export default Contact;
