import React from "react";
import { connect } from "react-redux";
import { Redirect } from "react-router-dom";
import Hoc from "../hoc/hoc";
import { createStore } from 'redux'
import './../assets/style.css'
import * as messageActions from "./../store/actions/message";
import groupUser from './../assets/groupUser.png'
import singleUser from './../assets/singleUser.png'

class Profile extends React.Component {
  picFlag=''
  replaceAll=(string, search, replace)=> {
    return string.split(search).join(replace);
  }
  getUserName = () => {
    var itemNO = this.props.id.split('_').length

    var name = this.props.id
    name = this.replaceAll(name,'_',' ')
    var username = localStorage.getItem('username')
    if (username !== null & username !== undefined)
    {
      username = localStorage.getItem('username').charAt(0).toUpperCase() + username.slice(1)  
      }
      name = name.replace(username, '')
   
      if (itemNO > 2)
      {
        name+=' [Group]'
      }
  
     
      return name
  }
  state = {
    loc: '',
    value: '',
    picFlag:''
  }

  getImageURL = (name) => {

    let url=''
    var token=localStorage.getItem('token')
    var token = 'Token ' + localStorage.getItem('token');
    var value = fetch(`http://localhost:8000/chat/profile/${name}`, {
      method: 'GET',
        headers: {
            'Authorization': token,
            'Content-Type': 'application/json'
        }
    })
      .then(res => res.json())

      .then(res =>
      { this.picFlag = res[0].photo_url }
        
        ).catch(
          console.log('error occur while loading pic for contact')
    )
    return this.picFlag
    
  }

  getImageDetail = () => {

    var itemNO = this.props.id.split('_').length
    var name = this.props.id
    var url=''
    // var name = members_name
    name = this.replaceAll(name,'_',' ')
    var username = localStorage.getItem('username')
    name = name.toLowerCase()
    name = name.replace(username, '')
    name = this.replaceAll(name, ' ', '')

    if (itemNO <= 2 & username !== null & username !== undefined)
    {
        url = this.getImageURL(name)
        
    }
    if (itemNO > 2)
    {
      url=groupUser
    }
    if (url === undefined)
    {
      url = singleUser
      
      }
    console.log('check this our',url)
    return url
  }
 

  render() {
    if (this.props.token === null) {
      return <Redirect to="/" />;
    }

    return (
      <div className="contact-profile" >
        
        {this.props.username !== null ? (
          <Hoc>
            
            <img
              src={this.getImageDetail()}

               />
            {this.state.loc}
            <p>{this.getUserName()}</p>
            <div className="social-media">
               {console.log(this.props.chats[0])}
              <i className="fa fa-facebook" aria-hidden="true" />
              <i className="fa fa-twitter" aria-hidden="true" />
              <i className="fa fa-instagram" aria-hidden="true" />
            </div>
          </Hoc>
        ) : null}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    token: state.auth.token,
    username: state.auth.username,
    chats: state.message.chats,
    
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getUserChats: (username, token) =>
      dispatch(messageActions.getUserChats(username, token)),
    getChats: () => {
      
      // return window.location.pathname
    }
    
  };
};


export default connect(mapStateToProps)(Profile);
