import React from "react";
import { Spin, Icon } from "antd";
import { connect } from "react-redux";
import * as actions from "../store/actions/auth";
import * as navActions from "../store/actions/nav";
import * as messageActions from "../store/actions/message";
import Contact from "../components/Contact";
import './../assets/style.css'
import groupUser from './../assets/groupUser.png'
import singleUser from './../assets/singleUser.png'

const antIcon = <Icon type="loading" style={{ fontSize: 24 }} spin />;

class Sidepanel extends React.Component {
  state = {
    loginForm: true,
    chat: {},
    value: ''
    
  };

  getImage = () => {

    var username = localStorage.getItem('username')
    fetch(`http://localhost:8000/chat/profile/${username}`)
      .then(res => res.json())
      .then(res => this.setState({ value: (res[0].photo_url) }))
  }

  waitForAuthDetails() {
    const component = this;
    setTimeout(function () {
      if (
        component.props.token !== null &&
        component.props.token !== undefined
      ) {
        component.props.getUserChats(
          component.props.username,
          component.props.token
        );
        return;
      } else {
        console.log("waiting for authentication details...");
        component.waitForAuthDetails();
      }
    }, 100);
  }

  componentDidMount() {
    this.waitForAuthDetails();
  }

  
  openAddChatPopup() {
    this.props.addChat();
  }

  changeForm = () => {
    this.setState({ loginForm: !this.state.loginForm });
  };

  authenticate = e => {
    e.preventDefault();
    if (this.state.loginForm) {
      this.props.login(e.target.username.value, e.target.password.value);
    } else {
      this.props.signup(
        e.target.username.value,
        e.target.email.value,
        e.target.password.value,
        e.target.password2.value
      );
    }
  };

  getUsername = () => {
    var getUser = localStorage.getItem('username')
    
    if (getUser !== undefined && getUser !== null) {
      var name = getUser.charAt(0).toUpperCase() + getUser.slice(1)
      return name
    }
    // fetch(`http://localhost:8000/chat/${id}`)
    //   .then(response => response.json())
    //   .then(res=>{this.setState({chat:res})})
        
    // .then(data=>data)
    // console.log('okkkkkkkkkkkkk',obj)
    // // this.setState({id:obj.id})
    // return obj.id
  }

  getImageURL = (username) => {

    // console.log(username)
    var value = ''
    fetch(`http://localhost:8000/chat/profile/${username}`)
      .then(res => res.json())

      .then(res =>(
        value = res[0].photo_url
      )

      ).catch(
        console.log('error while connecting profile link')
      )
    return value
  }


  replaceAll=(string, search, replace)=> {
    return string.split(search).join(replace);
  }

  getImageDetail = (members_name) => {
  
    var itemNO = members_name.split('_').length
    var url=''
    var name = members_name
    name = this.replaceAll(name,'_',' ')
    var username = localStorage.getItem('username')
    name = name.toLowerCase()
    name = name.replace(username, '')
    name = name.replace(' ', '')
    
    if (itemNO <= 2 & username !== null & username !== undefined)
    {

      url = this.getImageURL(name)
      if (url === '')
      {
            url=singleUser
      }


    }
    if (itemNO > 2)
    {
      url=groupUser
    }
  
  }
  
  
  render() {
    let activeChats = this.props.chats.map(c => {
      
      return (
        <div key={c.id}> 
          
          <Contact
            key={c.id}
            name={c.memebers_name}
            picURL={this.getImageDetail(c.memebers_name)}
            status="busy"
            chatURL={`${c.memebers_name}`}
            />
        </div>
          
      );
    } );
    return (
      <div id="sidepanel">
        <div id="profile"  >
          <div className="wrap" >
            
            {
              localStorage.getItem('username') === null ?
                <p style={{ fontSize: '18px' }}>Enter Login info here</p> :
                <div>
                  
                <img
                  id="profile-img"
                  src={this.state.value}
                  className="online"
                  alt=""    
                  />
                {this.getImage()}
                </div>
              
            
            
            }
            
            <p style={{fontSize:'25px'}}>{this.getUsername()}</p>
            <i
              className="fa fa-chevron-down expand-button"
              aria-hidden="true"
            />
            <div id="status-options">
              <ul>
                <li id="status-online" className="active">
                  <span className="status-circle" /> <p>Online</p>
                </li>
                <li id="status-away">
                  <span className="status-circle" /> <p>Away</p>
                </li>
                <li id="status-busy">
                  <span className="status-circle" /> <p>Busy</p>
                </li>
                <li id="status-offline">
                  <span className="status-circle" /> <p>Offline</p>
                </li>
              </ul>
            </div>
            <div id="expanded">
              {this.props.loading ? (
                <Spin indicator={antIcon} />
              ) : this.props.isAuthenticated ? (
                  <button onClick={() => {
                     window.location.reload(false);
                    this.props.logout()
                  }} className="btnTry">
                  <span className='btnTry'>Logout</span>
                </button>
              ) : (
                <div style={{fontSize:'15px'}}>
                  <form method="POST" onSubmit={this.authenticate}>
                    {this.state.loginForm ? (
                      <div>
                        <input
                          name="username"
                          type="text"
                          placeholder="username"
                        />
                        <input
                          name="password"
                          type="password"
                          placeholder="password"
                        />
                      </div>
                    ) : (
                      <div>
                        <input
                          name="username"
                          type="text"
                          placeholder="username"
                        />
                        <input name="email" type="email" placeholder="email" />
                        <input
                          name="password"
                          type="password"
                          placeholder="password"
                        />
                        <input
                          name="password2"
                          type="password"
                          placeholder="password confirm"
                        />
                      </div>
                    )}

                        <button type="submit"  >Authenticate</button>
                  </form>

                  <button onClick={this.changeForm}>Switch</button>
                </div>
                  )}
              
              {this.props.error!==null?<p style={{fontSize:'15px'}}>Wrong Username or Password..!</p>:<p></p>}
              <hr style={{marginTop:'90px',marginRight:'20px'}}/>
            </div>
            <div  style={{marginBottom:'200px'}} id="contacts">
            <ul >{activeChats}</ul>

       
              </div>
          </div>
        </div>
        
        
        {
          localStorage.getItem('username') === null ? <p></p> :
            <div >
              {/* <div  id="contacts">
                 <ul >{activeChats}</ul>
              </div> */}
            <div id="bottom-bar" style={{marginTop:'200px'}} >
              <button id="addChat" style={{width:'100%',fontSize:'15px'}} onClick={() => this.openAddChatPopup()}>
                <i className="fa fa-user-plus fa-fw" aria-hidden="true" />
                <span>Create chat</span>
              </button>
              
              </div>
              </div>
        }
        
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null,
    loading: state.auth.loading,
    token: state.auth.token,
    username: state.auth.username,
    chats: state.message.chats,
    error:state.auth.error
  };
};

const mapDispatchToProps = dispatch => {
  return {
    login: (userName, password) =>
      // dispatch(actions.authLogin(localStorage.getItem('name'), localStorage.getItem('password'))),
      dispatch(actions.authLogin(userName, password)),
    logout: () => dispatch(actions.logout()),
    signup: (username, email, password1, password2) =>
      dispatch(actions.authSignup(username, email, password1, password2)),
    addChat: () => dispatch(navActions.openAddChatPopup()),
    getUserChats: (username, token) =>
      dispatch(messageActions.getUserChats(username, token))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Sidepanel);
