    // console.log(name)
    // if (itemNO <= 2 & username !== null & username !== undefined)
    // {
    //     // url = this.getImageURL(name)
        
    // }
    // if (itemNO > 2)
    // {
    //   url=groupUser
    // }
    // if (url === undefined)
    // {
    //   url = singleUser
      
    //   }
    // console.log('check this our',url)
    // return url
  }
 

  render() {
    if (this.props.token === null) {
      return <Redirect to="/" />;
    }

    return (
      <div className="contact-profile" >
        
        {this.props.username !== null ? (
          <Hoc>
            
            <img
              src={this.getImageDetail()}

            />
            {console.log(this.getImageDetail())}
            {this.state.loc}
            <p>{this.getUserName()}</p>
            <div className="social-media">
              
              <i className="fa fa-facebook" aria-hidden="true" />
              <i className="fa fa-twitter" aria-hidden="true" />
              <i className="fa fa-instagram" aria-hidden="true" />
            </div>
          </Hoc>
        ) : null}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    token: state.auth.token,
    username: state.auth.username,
    chats: state.message.chats,
    
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getUserChats: (username, token) =>
      dispatch(messageActions.getUserChats(username, token)),
    getChats: () => {
      
      // return window.location.pathname
    }
    
  };
};


export default connect(mapStateToProps)(Profile);
