import { NavLink } from "react-router-dom";
import axios from "axios";
import React, { Component } from 'react'
import './../assets/style.css'
import groupUser from './../assets/groupUser.png'
import singleUser from './../assets/singleUser.png'

export class Contact extends Component {
  state = {
    
    chat: [],
    id:null,
    imgUrl: '',
    picFlag:''
    
    
  }   

  getImageURL = (name) => {

    let url = ''
    var token=localStorage.getItem('token')
    axios.defaults.headers = {
      "Content-Type": "application/json",
      Authorization: `Token ${this.props.token}`
    };
    var value= fetch(`http://localhost:8000/chat/profile/${name}`)
      .then(res => res.json())

      .then(res => (
        this.picFlag=res[0].photo_url 
      )
      
    ).catch(
          console.log('error occur while loading pic for contact')
    )
    return this.picFlag
    
  }


  replaceAll=(string, search, replace)=> {
    return string.split(search).join(replace);
  }

  getImageDetail = () => {
    var itemNO = this.props.chatURL.split('_').length
    var name = this.props.chatURL
    var url=''
    // var name = members_name
    name = this.replaceAll(name,'_',' ')
    var username = localStorage.getItem('username')
    name = name.toLowerCase()
    name = name.replace(username, '')
    name = this.replaceAll(name,' ','')
    if (itemNO <= 2 & username !== null & username !== undefined)
    {
        url = this.getImageURL(name)
        
    }
    if (itemNO > 2)
    {
      url=groupUser
    }
    
    return url
  }
 
  getName = () => {

    var itemNO = this.props.chatURL.split('_').length
    var name = this.props.chatURL
    // name = name.replace(localStorage.getItem('username'), '')
    name = this.replaceAll(name,'_',' ')
    // name = name.replace('_', ' ')
    var username = localStorage.getItem('username')
    var tempUsername=''
    if (username !== null & username !== undefined)
    {
      tempUsername=name
      username = localStorage.getItem('username').charAt(0).toUpperCase() + username.slice(1)  

      }
      
    name = name.replace(username, '')
    tempUsername = tempUsername.replace(username, '')
    
    if (itemNO > 2)
    {
      name+=' [Group]'
    }

   
    return name
  }


  render() {
    return (
      <div className='wrapTry' style={{fontSize:'15px'}}>
        
                
      <NavLink  to={`/${this.props.chatURL}`} style={{ color: "#fff" }}>      
      <li   >
        {localStorage.getItem('token') !== null ?
              <div > 
            <img style={{
            width: '50px',
            borderRadius: "50%",
            float: 'left',
            marginRight: '10px'
                }}
                  // src='http://emilcarlsson.se/assets/louislitt.png'
                  src={this.getImageDetail()}
                  // src={this.props.picURL}
                />
                
                <div className="meta">
                  <p className="name" >   
                    {this.getName()}
                  </p>
          </div>
        </div>
          :
    <p></p>
    
 
        }
    
        
</li>

        </NavLink>
        <br/>
</div>
    )
  }
}


export default Contact;
