import { NavLink } from "react-router-dom";
import axios from "axios";
import React, { Component } from 'react'
import './../assets/style.css'

export class Contact extends Component {
  state = {
    
    chat: [],
    id:null,
    imgUrl:''
    
  }  
  getImage = (name) => {

    // var url={}
    fetch(`http://localhost:8000/chat/profile/${name}`)
    .then(res => res.json())
    .then(res => this.setState( {imgUrl:(res[0].photo_url)}))
  }
  
   replaceAll=(string, search, replace)=> {
    return string.split(search).join(replace);
  }
  getName = () => {

    var itemNO = this.props.chatURL.split('_').length
    var name = this.props.chatURL
    // name = name.replace(localStorage.getItem('username'), '')
    name = this.replaceAll(name,'_',' ')
    // name = name.replace('_', ' ')
    var username = localStorage.getItem('username')
    var tempUsername=''
    if (username !== null & username !== undefined)
    {
      tempUsername=username
      username = localStorage.getItem('username').charAt(0).toUpperCase() + username.slice(1)  

      }
      
    name = name.replace(username, '')
    tempUsername = tempUsername.replace(username, '')
    if (username !== null & username !== undefined)
    {
      this.getImage() 
    }
    
    
    if (itemNO > 2)
    {
      name+=' [Group]'
    }

   
    return name
  }


  render() {
    return (
      <div className='wrapTry' style={{fontSize:'15px'}}>
        
                
      <NavLink  to={`/${this.props.chatURL}`} style={{ color: "#fff" }}>      
      <li   >
        {localStorage.getItem('token') !== null ?
              <div > 
            <img style={{
            width: '50px',
            borderRadius: "50%",
            float: 'left',
            marginRight: '10px'
                }}
                  src={this.props.picURL} alt=""
             />
                <div className="meta">
                  <p className="name" >   
                    {this.getName()}
                  </p>
          </div>
        </div>
          :
    <p></p>
    
 
        }
    
        
</li>

        </NavLink>
        <br/>
</div>
    )
  }
}


export default Contact;
